import React, { useState } from 'react';
import '../stockinput.css';

const StockInput = ({ onAddStock }) => {
  const [stockName, setStockName] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (stockName) {
      setError(null); // Reset error
      await onAddStock(stockName.toUpperCase()); // Call the function passed from App
      setStockName(''); // Clear input field
    } else {
      setError('Please enter a stock name.');
    }
  };

  return (
    <div className="stock-input">
      <form onSubmit={handleSubmit} className="form__group field">
        <div className="form__container">
          {/* Updated input element */}
          <input
            type="text"
            autoComplete="off"
            name="text"
            className="input" // Updated class name for the new input style
            value={stockName}
            onChange={(e) => setStockName(e.target.value)}
            placeholder="Username" // Updated placeholder
            required
          />
          <button> type="submit" className="button">Add Stock</button> {/* Button element */}
        </div>
      </form>
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default StockInput;
